actionConfiguration.body:
async () => {
  const products = await getProducts.run();
  const categoryFilter = sel_category.selectedOptionValue;
  let filteredProducts = products;
  if (categoryFilter) {
    filteredProducts = products.filter(p => p.category === categoryFilter);
  }
  return filteredProducts.map(p => {
    return {
      ID: utils.idConverter(p.id),
      Name: p.name,
      SKU: p.sku,
      Category: p.category,
      UnitPrice: p.price.toLocaleString('en-US', {
        style: 'currency',
        currency: 'USD'
      }),
      Stock: p.total_stock,
      ProductID: p.id
    };
  });
}

